import { Pool, neonConfig } from '@neondatabase/serverless';
import { drizzle } from 'drizzle-orm/neon-serverless';
import ws from "ws";
import * as schema from "@shared/schema";

neonConfig.webSocketConstructor = ws;

if (!process.env.DATABASE_URL) {
  throw new Error(
    "DATABASE_URL must be set. Did you forget to provision a database?",
  );
}

export const pool = new Pool({ connectionString: process.env.DATABASE_URL });
export const db = drizzle({ client: pool, schema });

server/index.ts.content:
import express, { type Request, Response, NextFunction } from "express";
import { registerRoutes } from "./routes";
import { setupVite, serveStatic, log } from "./vite";

const app = express();
app.use(express.json());
app.use(express.urlencoded({ extended: false }));

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "â€¦";
      }

      log(logLine);
    }
  });

  next();
});

(async () => {
  const server = await registerRoutes(app);

  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";

    res.status(status).json({ message });
    throw err;
  });

  // importantly only setup vite in development and after
  // setting up all the other routes so the catch-all route
  // doesn't interfere with the other routes
  if (app.get("env") === "development") {
    await setupVite(app, server);
  } else {
    serveStatic(app);
  }

  // ALWAYS serve the app on the port specified in the environment variable PORT
  // Other ports are firewalled. Default to 5000 if not specified.
  // this serves both the API and the client.
  // It is the only port that is not firewalled.
  const port = parseInt(process.env.PORT || '5000', 10);
  server.listen({
    port,
    host: "0.0.0.0",
    reusePort: true,
  }, () => {
    log(`serving on port ${port}`);
  });
})();

server/replitAuth.ts.content:
import * as client from "openid-client";
import { Strategy, type VerifyFunction } from "openid-client/passport";

import passport from "passport";
import session from "express-session";
import type { Express, RequestHandler } from "express";
import memoize from "memoizee";
import connectPg from "connect-pg-simple";
import { storage } from "./storage";

if (!process.env.REPLIT_DOMAINS) {
  throw new Error("Environment variable REPLIT_DOMAINS not provided");
}

const getOidcConfig = memoize(
  async () => {
    return await client.discovery(
      new URL(process.env.ISSUER_URL ?? "https://replit.com/oidc"),
      process.env.REPL_ID!
    );
  },
  { maxAge: 3600 * 1000 }
);

export function getSession() {
  const sessionTtl = 7 * 24 * 60 * 60 * 1000; // 1 week

  // For development, use simpler session setup
  if (process.env.NODE_ENV === 'development') {
    return session({
      secret: process.env.SESSION_SECRET || 'dev-secret-key-123',
      resave: false,
      saveUninitialized: false,
      cookie: {
        httpOnly: true,
        secure: false, // Allow HTTP in development
        maxAge: sessionTtl,
      },
    });
  }

  const pgStore = connectPg(session);
  const sessionStore = new pgStore({
    conString: process.env.DATABASE_URL,
    createTableIfMissing: false,
    ttl: sessionTtl,
    tableName: "sessions",
  });
  return session({
    secret: process.env.SESSION_SECRET!,
    store: sessionStore,
    resave: false,
    saveUninitialized: false,
    cookie: {
      httpOnly: true,
      secure: true,
      maxAge: sessionTtl,
    },
  });
}

function updateUserSession(
  user: any,
  tokens: client.TokenEndpointResponse & client.TokenEndpointResponseHelpers
) {
  user.claims = tokens.claims();
  user.access_token = tokens.access_token;
  user.refresh_token = tokens.refresh_token;
  user.expires_at = user.claims?.exp;
}

async function upsertUser(
  claims: any,
) {
  await storage.upsertUser({
    email: claims["email"],
    firstName: claims["first_name"],
    lastName: claims["last_name"],
    profileImageUrl: claims["profile_image_url"],
  });
}

export async function setupAuth(app: Express) {
  app.set("trust proxy", 1);

  // Skip complex auth setup in development
  if (process.env.NODE_ENV === 'development') {
    // Simple session for role switching
    app.use(session({
      secret: 'dev-secret-123',
      resave: false,
      saveUninitialized: true,
      cookie: { secure: false }
    }));
    return;
  }

  app.use(getSession());
  app.use(passport.initialize());
  app.use(passport.session());

  const config = await getOidcConfig();

  const verify: VerifyFunction = async (
    tokens: client.TokenEndpointResponse & client.TokenEndpointResponseHelpers,
    verified: passport.AuthenticateCallback
  ) => {
    const user = {};
    updateUserSession(user, tokens);
    await upsertUser(tokens.claims());
    verified(null, user);
  };

  for (const domain of process.env
    .REPLIT_DOMAINS!.split(",")) {
    const strategy = new Strategy(
      {
        name: `replitauth:${domain}`,
        config,
        scope: "openid email profile offline_access",
        callbackURL: `https://${domain}/api/callback`,
      },
      verify,
    );
    passport.use(strategy);
  }

  passport.serializeUser((user: Express.User, cb) => cb(null, user));
  passport.deserializeUser((user: Express.User, cb) => cb(null, user));

  app.get("/api/login", (req, res, next) => {
    passport.authenticate(`replitauth:${req.hostname}`, {
      prompt: "login consent",
      scope: ["openid", "email", "profile", "offline_access"],
    })(req, res, next);
  });

  app.get("/api/callback", (req, res, next) => {
    passport.authenticate(`replitauth:${req.hostname}`, {
      successReturnToOrRedirect: "/",
      failureRedirect: "/api/login",
    })(req, res, next);
  });

  app.get("/api/logout", (req, res) => {
    req.logout(() => {
      res.redirect(
        client.buildEndSessionUrl(config, {
          client_id: process.env.REPL_ID!,
          post_logout_redirect_uri: `${req.protocol}://${req.hostname}`,
        }).href
      );
    });
  });
}

export const isAuthenticated: RequestHandler = async (req, res, next) => {
  // For development purposes - bypass authentication
  if (process.env.NODE_ENV === 'development') {
    // Get current user from session or default to admin
    const currentUserId = (req.session as any)?.currentUserId || 'admin-001';
    (req as any).user = {
      claims: {
        sub: currentUserId
      }
    };
    return next();
  }

  const user = req.user as any;

  if (!req.isAuthenticated() || !user.expires_at) {
    return res.status(401).json({ message: "Unauthorized" });
  }

  const now = Math.floor(Date.now() / 1000);
  if (now <= user.expires_at) {
    return next();
  }

  const refreshToken = user.refresh_token;
  if (!refreshToken) {
    res.status(401).json({ message: "Unauthorized" });
    return;
  }

  try {
    const config = await getOidcConfig();
    const tokenResponse = await client.refreshTokenGrant(config, refreshToken);
    updateUserSession(user, tokenResponse);
    return next();
  } catch (error) {
    res.status(401).json({ message: "Unauthorized" });
    return;
  }
};

server/routes.ts.content:
import type { Express } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import { setupAuth, isAuthenticated } from "./replitAuth";
import { 
  insertVendorSchema, 
  insertMenuCategorySchema, 
  insertMenuItemSchema, 
  insertOrderSchema, 
  insertOrderItemSchema
} from "@shared/schema";
import { z } from "zod";

export async function registerRoutes(app: Express): Promise<Server> {
  // Auth middleware
  await setupAuth(app);

  // Development authentication
  app.post('/api/auth/login', async (req: any, res) => {
    const { email, password } = req.body;

    // Simple credential check for development
    const credentials = {
      'admin@bitsgoa.ac.in': { password: 'admin123', userId: 'admin-001' },
      'vendor@bitsgoa.ac.in': { password: 'vendor123', userId: 'vendor-001' },
      'student@bitsgoa.ac.in': { password: 'user123', userId: 'user-001' }
    };

    const userCred = credentials[email as keyof typeof credentials];
    if (!userCred || userCred.password !== password) {
      return res.status(401).json({ message: "Invalid credentials" });
    }

    // Store user in session
    req.session.currentUserId = userCred.userId;

    const user = await storage.getUser(userCred.userId);
    res.json(user);
  });

  app.post('/api/auth/logout', (req: any, res) => {
    req.session.currentUserId = null;
    res.json({ message: "Logged out successfully" });
  });

  // Development role switcher
  app.post('/api/dev/switch-role', async (req: any, res) => {
    const { userId } = req.body;
    const validUserIds = ['user-001', 'vendor-001', 'admin-001'];

    if (!validUserIds.includes(userId)) {
      return res.status(400).json({ message: "Invalid user ID" });
    }

    // Store current user in session
    req.session.currentUserId = userId;

    const user = await storage.getUser(userId);
    res.json(user);
  });

  // Auth routes
  app.get('/api/auth/user', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const user = await storage.getUser(userId);
      res.json(user);
    } catch (error) {
      console.error("Error fetching user:", error);
      res.status(500).json({ message: "Failed to fetch user" });
    }
  });

  // User role check middleware
  const requireRole = (roles: string[]) => {
    return async (req: any, res: any, next: any) => {
      try {
        const userId = req.user.claims.sub;
        const user = await storage.getUser(userId);

        if (!user || !roles.includes(user.role || "user")) {
          return res.status(403).json({ message: "Insufficient permissions" });
        }

        req.currentUser = user;
        next();
      } catch (error) {
        res.status(500).json({ message: "Error checking permissions" });
      }
    };
  };

  // Middleware to populate currentUser for all authenticated routes
  const populateCurrentUser = async (req: any, res: any, next: any) => {
    try {
      const userId = req.user.claims.sub;
      const user = await storage.getUser(userId);
      req.currentUser = user;
      next();
    } catch (error) {
      res.status(500).json({ message: "Error fetching user data" });
    }
  };

  // Vendor routes
  app.get('/api/vendors', async (req, res) => {
    try {
      const vendors = await storage.getVendors();
      res.json(vendors);
    } catch (error) {
      console.error("Error fetching vendors:", error);
      res.status(500).json({ message: "Failed to fetch vendors" });
    }
  });

  app.get('/api/vendors/:id', async (req, res) => {
    try {
      const vendor = await storage.getVendor(req.params.id);
      if (!vendor) {
        return res.status(404).json({ message: "Vendor not found" });
      }
      res.json(vendor);
    } catch (error) {
      console.error("Error fetching vendor:", error);
      res.status(500).json({ message: "Failed to fetch vendor" });
    }
  });

  app.post('/api/vendors', isAuthenticated, requireRole(['admin']), async (req: any, res) => {
    try {
      const validatedData = insertVendorSchema.parse(req.body);
      const vendor = await storage.createVendor(validatedData);
      res.status(201).json(vendor);
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ message: "Invalid vendor data", errors: error.errors });
      }
      console.error("Error creating vendor:", error);
      res.status(500).json({ message: "Failed to create vendor" });
    }
  });

  app.get('/api/vendors/:id/menu', async (req, res) => {
    try {
      const menuItems = await storage.getMenuItems(req.params.id);
      res.json(menuItems);
    } catch (error) {
      console.error("Error fetching menu items:", error);
      res.status(500).json({ message: "Failed to fetch menu items" });
    }
  });

  app.post('/api/vendors/:id/menu-categories', isAuthenticated, requireRole(['vendor', 'admin']), async (req: any, res) => {
    try {
      const validatedData = insertMenuCategorySchema.parse({
        ...req.body,
        vendorId: req.params.id
      });

      // Check if user owns this vendor or is admin
      if (req.currentUser.role !== 'admin') {
        const vendor = await storage.getVendorByUserId(req.currentUser.id);
        if (!vendor || vendor.id !== req.params.id) {
          return res.status(403).json({ message: "Access denied" });
        }
      }

      const category = await storage.createMenuCategory(validatedData);
      res.status(201).json(category);
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ message: "Invalid category data", errors: error.errors });
      }
      console.error("Error creating menu category:", error);
      res.status(500).json({ message: "Failed to create menu category" });
    }
  });

  app.post('/api/vendors/:id/menu-items', isAuthenticated, requireRole(['vendor', 'admin']), async (req: any, res) => {
    try {
      const validatedData = insertMenuItemSchema.parse({
        ...req.body,
        vendorId: req.params.id
      });

      // Check if user owns this vendor or is admin
      if (req.currentUser.role !== 'admin') {
        const vendor = await storage.getVendorByUserId(req.currentUser.id);
        if (!vendor || vendor.id !== req.params.id) {
          return res.status(403).json({ message: "Access denied" });
        }
      }

      const menuItem = await storage.createMenuItem(validatedData);
      res.status(201).json(menuItem);
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ message: "Invalid menu item data", errors: error.errors });
      }
      console.error("Error creating menu item:", error);
      res.status(500).json({ message: "Failed to create menu item" });
    }
  });

  app.patch('/api/menu-items/:id', isAuthenticated, requireRole(['vendor', 'admin']), async (req: any, res) => {
    try {
      const menuItem = await storage.getMenuItem(req.params.id);
      if (!menuItem) {
        return res.status(404).json({ message: "Menu item not found" });
      }

      // Check if user owns this vendor or is admin
      if (req.currentUser.role !== 'admin') {
        const vendor = await storage.getVendorByUserId(req.currentUser.id);
        if (!vendor || vendor.id !== menuItem.vendorId) {
          return res.status(403).json({ message: "Access denied" });
        }
      }

      const updatedItem = await storage.updateMenuItem(req.params.id, req.body);
      res.json(updatedItem);
    } catch (error) {
      console.error("Error updating menu item:", error);
      res.status(500).json({ message: "Failed to update menu item" });
    }
  });

  // Order routes
  app.get('/api/orders', isAuthenticated, populateCurrentUser, async (req: any, res) => {
    try {
      const user = req.currentUser;

      if (!user) {
        return res.status(404).json({ message: "User not found" });
      }

      let orders;

      if (user.role === 'admin') {
        orders = await storage.getOrders();
      } else if (user.role === 'vendor') {
        const vendor = await storage.getVendorByUserId(user.id);
        if (!vendor) {
          return res.status(404).json({ message: "Vendor profile not found" });
        }
        orders = await storage.getOrders(undefined, vendor.id);
      } else {
        orders = await storage.getOrders(user.id);
      }

      res.json(orders);
    } catch (error) {
      console.error("Error fetching orders:", error);
      res.status(500).json({ message: "Failed to fetch orders" });
    }
  });

  app.post('/api/orders', isAuthenticated, requireRole(['user']), async (req: any, res) => {
    try {
      const orderData = insertOrderSchema.parse({
        ...req.body,
        userId: req.currentUser.id
      });

      const order = await storage.createOrder(orderData);

      // Create order items
      const orderItemsData = req.body.items || [];
      for (const item of orderItemsData) {
        const orderItemData = insertOrderItemSchema.parse({
          ...item,
          orderId: order.id
        });
        await storage.createOrderItem(orderItemData);
      }

      res.status(201).json(order);
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ message: "Invalid order data", errors: error.errors });
      }
      console.error("Error creating order:", error);
      res.status(500).json({ message: "Failed to create order" });
    }
  });

  app.patch('/api/orders/:id/status', isAuthenticated, requireRole(['vendor', 'admin']), async (req: any, res) => {
    try {
      const { status } = req.body;
      const order = await storage.getOrder(req.params.id);

      if (!order) {
        return res.status(404).json({ message: "Order not found" });
      }

      // Check if user owns this vendor or is admin
      if (req.currentUser.role !== 'admin') {
        const vendor = await storage.getVendorByUserId(req.currentUser.id);
        if (!vendor || vendor.id !== order.vendorId) {
          return res.status(403).json({ message: "Access denied" });
        }
      }

      const updatedOrder = await storage.updateOrderStatus(req.params.id, status);
      res.json(updatedOrder);
    } catch (error) {
      console.error("Error updating order status:", error);
      res.status(500).json({ message: "Failed to update order status" });
    }
  });

  app.get('/api/orders/:id/items', async (req, res) => {
    try {
      const orderItems = await storage.getOrderItems(req.params.id);
      res.json(orderItems);
    } catch (error) {
      console.error("Error fetching order items:", error);
      res.status(500).json({ message: "Failed to fetch order items" });
    }
  });

  // Review routes removed

  // Analytics routes
  app.get('/api/vendor-stats', isAuthenticated, requireRole(['vendor']), async (req: any, res) => {
    try {
      const vendor = await storage.getVendorByUserId(req.currentUser.id);
      if (!vendor) {
        return res.status(404).json({ message: "Vendor profile not found" });
      }

      const stats = await storage.getVendorStats(vendor.id);
      res.json(stats);
    } catch (error) {
      console.error("Error fetching vendor stats:", error);
      res.status(500).json({ message: "Failed to fetch vendor stats" });
    }
  });

  app.get('/api/admin-stats', isAuthenticated, requireRole(['admin']), async (req: any, res) => {
    try {
      const stats = await storage.getAdminStats();
      res.json(stats);
    } catch (error) {
      console.error("Error fetching admin stats:", error);
      res.status(500).json({ message: "Failed to fetch admin stats" });
    }
  });

  app.get('/api/top-vendors', isAuthenticated, requireRole(['admin']), async (req: any, res) => {
    try {
      const topVendors = await storage.getTopVendors();
      res.json(topVendors);
    } catch (error) {
      console.error("Error fetching top vendors:", error);
      res.status(500).json({ message: "Failed to fetch top vendors" });
    }
  });

  // Search routes
  app.get('/api/search', async (req, res) => {
    try {
      const { q } = req.query;
      if (!q || typeof q !== 'string') {
        return res.status(400).json({ message: "Search query required" });
      }

      const menuItems = await storage.searchMenuItems(q);
      res.json(menuItems);
    } catch (error) {
      console.error("Error searching menu items:", error);
      res.status(500).json({ message: "Failed to search menu items" });
    }
  });

  const httpServer = createServer(app);
  return httpServer;
}

server/storage.ts.content:
import {
  users,
  vendors,
  menuCategories,
  menuItems,
  orders,
  orderItems,
  type User,
  type UpsertUser,
  type Vendor,
  type InsertVendor,
  type MenuCategory,
  type InsertMenuCategory,
  type MenuItem,
  type InsertMenuItem,
  type Order,
  type InsertOrder,
  type OrderItem,
  type InsertOrderItem,
} from "@shared/schema";
import { db } from "./db";
import { eq, desc, and, avg, count, sum, sql, gte, like } from "drizzle-orm";

export interface IStorage {
  // User operations
  getUser(id: string): Promise<User | undefined>;
  upsertUser(user: UpsertUser): Promise<User>;

  // Vendor operations
  getVendors(): Promise<Vendor[]>;
  getVendor(id: string): Promise<Vendor | undefined>;
  getVendorByUserId(userId: string): Promise<Vendor | undefined>;
  createVendor(vendor: InsertVendor): Promise<Vendor>;
  updateVendor(id: string, vendor: Partial<InsertVendor>): Promise<Vendor>;

  // Menu operations
  getMenuCategories(vendorId: string): Promise<MenuCategory[]>;
  createMenuCategory(category: InsertMenuCategory): Promise<MenuCategory>;
  getMenuItems(vendorId: string): Promise<MenuItem[]>;
  getMenuItem(id: string): Promise<MenuItem | undefined>;
  createMenuItem(item: InsertMenuItem): Promise<MenuItem>;
  updateMenuItem(id: string, item: Partial<InsertMenuItem>): Promise<MenuItem>;
  searchMenuItems(query: string): Promise<MenuItem[]>;

  // Order operations
  getOrders(userId?: string, vendorId?: string): Promise<Order[]>;
  getOrder(id: string): Promise<Order | undefined>;
  createOrder(order: InsertOrder): Promise<Order>;
  updateOrderStatus(id: string, status: string): Promise<Order>;
  getOrderItems(orderId: string): Promise<OrderItem[]>;
  createOrderItem(item: InsertOrderItem): Promise<OrderItem>;

  // Review operations removed

  // Analytics operations
  getVendorStats(vendorId: string): Promise<any>;
  getAdminStats(): Promise<any>;
  getTopVendors(): Promise<any[]>;
}

export class DatabaseStorage implements IStorage {
  // User operations
  async getUser(id: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.id, id));
    return user;
  }

  async upsertUser(userData: UpsertUser): Promise<User> {
    const [user] = await db
      .insert(users)
      .values(userData)
      .onConflictDoUpdate({
        target: users.id,
        set: {
          ...userData,
          updatedAt: new Date(),
        },
      })
      .returning();
    return user;
  }

  // Vendor operations
  async getVendors(): Promise<Vendor[]> {
    return await db.select().from(vendors).where(eq(vendors.isActive, true)).orderBy(vendors.name);
  }

  async getVendor(id: string): Promise<Vendor | undefined> {
    const [vendor] = await db.select().from(vendors).where(eq(vendors.id, id));
    return vendor;
  }

  async getVendorByUserId(userId: string): Promise<Vendor | undefined> {
    const [vendor] = await db.select().from(vendors).where(eq(vendors.userId, userId));
    return vendor;
  }

  async createVendor(vendor: InsertVendor): Promise<Vendor> {
    const [newVendor] = await db.insert(vendors).values(vendor).returning();
    return newVendor;
  }

  async updateVendor(id: string, vendor: Partial<InsertVendor>): Promise<Vendor> {
    const [updatedVendor] = await db
      .update(vendors)
      .set({ ...vendor, updatedAt: new Date() })
      .where(eq(vendors.id, id))
      .returning();
    return updatedVendor;
  }

  // Menu operations
  async getMenuCategories(vendorId: string): Promise<MenuCategory[]> {
    return await db
      .select()
      .from(menuCategories)
      .where(and(eq(menuCategories.vendorId, vendorId), eq(menuCategories.isActive, true)))
      .orderBy(menuCategories.displayOrder);
  }

  async createMenuCategory(category: InsertMenuCategory): Promise<MenuCategory> {
    const [newCategory] = await db.insert(menuCategories).values(category).returning();
    return newCategory;
  }

  async getMenuItems(vendorId: string): Promise<MenuItem[]> {
    return await db
      .select()
      .from(menuItems)
      .where(eq(menuItems.vendorId, vendorId))
      .orderBy(menuItems.displayOrder);
  }

  async getMenuItem(id: string): Promise<MenuItem | undefined> {
    const [item] = await db.select().from(menuItems).where(eq(menuItems.id, id));
    return item;
  }

  async createMenuItem(item: InsertMenuItem): Promise<MenuItem> {
    const [newItem] = await db.insert(menuItems).values(item).returning();
    return newItem;
  }

  async updateMenuItem(id: string, item: Partial<InsertMenuItem>): Promise<MenuItem> {
    const [updatedItem] = await db
      .update(menuItems)
      .set({ ...item, updatedAt: new Date() })
      .where(eq(menuItems.id, id))
      .returning();
    return updatedItem;
  }

  async searchMenuItems(query: string): Promise<MenuItem[]> {
    return await db
      .select()
      .from(menuItems)
      .where(
        and(
          eq(menuItems.isAvailable, true),
          like(menuItems.name, `%${query}%`)
        )
      )
      .limit(20);
  }

  // Order operations
  async getOrders(userId?: string, vendorId?: string): Promise<Order[]> {
    if (userId) {
      return await db.select().from(orders)
        .where(eq(orders.userId, userId))
        .orderBy(desc(orders.createdAt));
    } else if (vendorId) {
      return await db.select().from(orders)
        .where(eq(orders.vendorId, vendorId))
        .orderBy(desc(orders.createdAt));
    }

    return await db.select().from(orders).orderBy(desc(orders.createdAt));
  }

  async getOrder(id: string): Promise<Order | undefined> {
    const [order] = await db.select().from(orders).where(eq(orders.id, id));
    return order;
  }

  async createOrder(order: InsertOrder): Promise<Order> {
    const [newOrder] = await db.insert(orders).values(order).returning();
    return newOrder;
  }

  async updateOrderStatus(id: string, status: string): Promise<Order> {
    const [updatedOrder] = await db
      .update(orders)
      .set({ status: status as any, updatedAt: new Date() })
      .where(eq(orders.id, id))
      .returning();
    return updatedOrder;
  }

  async getOrderItems(orderId: string): Promise<OrderItem[]> {
    return await db.select().from(orderItems).where(eq(orderItems.orderId, orderId));
  }

  async createOrderItem(item: InsertOrderItem): Promise<OrderItem> {
    const [newItem] = await db.insert(orderItems).values(item).returning();
    return newItem;
  }

  // Review operations removed

  // Analytics operations
  async getVendorStats(vendorId: string): Promise<any> {
    const today = new Date();
    today.setHours(0, 0, 0, 0);

    const [todayOrders] = await db
      .select({ count: count() })
      .from(orders)
      .where(and(
        eq(orders.vendorId, vendorId),
        gte(orders.createdAt, today)
      ));

    const [todayRevenue] = await db
      .select({ sum: sum(orders.totalAmount) })
      .from(orders)
      .where(and(
        eq(orders.vendorId, vendorId),
        gte(orders.createdAt, today),
        eq(orders.paymentStatus, "completed")
      ));

    const [pendingOrders] = await db
      .select({ count: count() })
      .from(orders)
      .where(and(
        eq(orders.vendorId, vendorId),
        sql`${orders.status} IN ('pending', 'confirmed', 'preparing')`
      ));

    // Average rating calculation removed - no reviews system

    return {
      todayOrders: todayOrders.count || 0,
      todayRevenue: parseFloat(todayRevenue.sum || "0"),
      pendingOrders: pendingOrders.count || 0,
      avgRating: 0, // No rating system
    };
  }

  async getAdminStats(): Promise<any> {
    const today = new Date();
    today.setHours(0, 0, 0, 0);

    const [todayOrders] = await db
      .select({ count: count() })
      .from(orders)
      .where(gte(orders.createdAt, today));

    const [todayRevenue] = await db
      .select({ sum: sum(orders.totalAmount) })
      .from(orders)
      .where(and(
        gte(orders.createdAt, today),
        eq(orders.paymentStatus, "completed")
      ));

    const [activeVendors] = await db
      .select({ count: count() })
      .from(vendors)
      .where(eq(vendors.isActive, true));

    const [totalUsers] = await db
      .select({ count: count() })
      .from(users);

    return {
      todayOrders: todayOrders.count || 0,
      todayRevenue: parseFloat(todayRevenue.sum || "0"),
      activeVendors: activeVendors.count || 0,
      totalUsers: totalUsers.count || 0,
    };
  }

  async getTopVendors(): Promise<any[]> {
    const today = new Date();
    today.setHours(0, 0, 0, 0);

    return await db
      .select({
        id: vendors.id,
        name: vendors.name,
        orders: count(orders.id),
        revenue: sum(orders.totalAmount),
        // rating: vendors.rating, // Rating removed
      })
      .from(vendors)
      .leftJoin(orders, and(
        eq(orders.vendorId, vendors.id),
        gte(orders.createdAt, today),
        eq(orders.paymentStatus, "completed")
      ))
      .where(eq(vendors.isActive, true))
      .groupBy(vendors.id, vendors.name)
      .orderBy(desc(count(orders.id)))
      .limit(10);
  }
}

export const storage = new DatabaseStorage();

